data() {
        return {
            # 分页的相关参数
            SumNum: 0,
            PageNum: 1,
            PageSize: 10,
            # 获取的全部信息，参数过滤后的信息，最终展示的信息
            AllInfoList: [],
            FilterInfoList: [],
            ShowInfoList: [],
            # 需要过滤的参数
